mapper.select:

        where s.del_flag = '0'

			AND subject_id = #{subjectId}


			AND parent_id = #{parentId}


			AND subject_name like concat('%', #{subjectName}, '%')


			AND status = #{status}

mapper.update:
update subjectclass
parent_id = #{parentId}, subject_name = #{subjectName}, ancestors = #{ancestors}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, email = #{email}, status = #{status}, update_by = #{updateBy}, update_time = sysdate()
where subject_id = #{subjectId}

update subjectclass set ancestors =

    when #{item.subjectId} then #{item.ancestors}

where subject_id in

    #{item.subjectId}
